import React, { useState } from 'react';
import { View, KeyboardAvoidingView, ImageBackground, TouchableOpacity, Text, TextInput, StyleSheet } from 'react-native';

export default function LoginScreen({ navigation }) {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [isRegistered, setIsRegistered] = useState(false);

  const handleSignIn = () => {
    if (email && password && isRegistered) {
      setMessage('Login Efetuado com Sucesso');
      clearInputs();
      navigation.navigate('Home');
    } else {
      setMessage('Por favor, corrija os dados ou crie uma conta');
    }
  };

  const handleSignUp = () => {
    if (username && email && password) {
      setMessage('Conta Criada com Sucesso');
      clearInputs();
      setIsRegistered(true);
      console.log('Dados do cadastro:', { username, email, password });
    } else {
      setMessage('Por favor, corrija os dados');
    }
  };

  const clearInputs = () => {
    setUsername('');
    setEmail('');
    setPassword('');
  };

  return (
    <KeyboardAvoidingView style={styles.background} behavior="padding">
      <ImageBackground style={styles.titleImage} source={require('.src/assets/fotoLogin.jpg')} resizeMode="contain">
        <View style={styles.overlay}>
          <View style={styles.formContainer}>

            <TextInput
              style={styles.input}
              onChangeText={(text) => setUsername(text)}
              value={username}
              placeholderTextColor="#000000"
              placeholder='Username'
              autoCorrect={false}
            />

            <TextInput
              style={[styles.input, email === '' && styles.errorInput]}
              onChangeText={(text) => setEmail(text)}
              value={email}
              placeholderTextColor="#000000"
              placeholder='Email'
              autoCorrect={false}
            />

            <TextInput
              style={[styles.input, password === '' && styles.errorInput]}
              onChangeText={(text) => setPassword(text)}
              value={password}
              placeholderTextColor="#000000"
              placeholder='Password'
              autoCorrect={false}
              secureTextEntry={true}
            />

            <TouchableOpacity style={styles.buttonLogin} onPress={handleSignIn}>
              <Text style={styles.buttonText}> Acessar </Text>
            </TouchableOpacity>

            <TouchableOpacity style={styles.buttonSignup} onPress={handleSignUp}>
              <Text style={styles.buttonText}> Criar conta </Text>
            </TouchableOpacity>

            {message !== '' && <Text style={styles.messageText}>{message}</Text>}

          </View>
        </View>
      </ImageBackground>
    </KeyboardAvoidingView>
  );
}

const styles = StyleSheet.create({
  background: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'flex-start',
    backgroundColor: '#000000',
  },
  titleImage: {
    flex: 1,
    width: '100%',
    marginBottom: 200,
    marginTop: -180,
  },
  overlay: {
    flex: 1,
    backgroundColor: 'rgba(0, 0, 0, 0.0)',
  },
  formContainer: {
    marginTop: 750,
    alignItems: 'center',
    marginBottom: 20,
  },
  input: {
    width: '70%',
    height: 30,
    backgroundColor: '#FFFFFF',
    marginBottom: 10,
    color: '#000000',
    paddingHorizontal: 10,
    borderRadius: 8,
  },
  buttonLogin: {
    backgroundColor: '#424242', 
    height: 30,
    width: '70%',
    borderRadius: 8,
    marginTop: 0,
    justifyContent: 'center',
  },
  buttonSignup: {
    backgroundColor: 'transparent',
    height: 30,
    width: 100,
    borderRadius: 8,
    marginTop: 15,
  },
  buttonText: {
    color: '#FFFFFF',
    textAlign: 'center',
    backgroundColor: 'transparent',
    fontSize: 15,
    marginTop: -5,
  },
  errorInput: {
    borderColor: '#DBA901',
    
  },
  labelError: {
    alignSelf: 'center',
    color: '#FF0000',
    marginBottom: 8,
  },
  messageText: {
    marginTop: 10,
    color: '#848484',
    fontSize: 15,
  },
});
