const SignUpScreen = ({ navigation }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  

  const validateFields = () => {
    if (!username.trim()) {
      Alert.alert('Erro', 'Por favor, insira o celular, nome de usuário ou email.');
      return false;
    }

    if (!password.trim() || !confirmPassword.trim()) {
      Alert.alert('Erro', 'Por favor, insira a senha.');
      return false;
    }

    if (password.length < 6) {
      Alert.alert('Erro', 'A senha deve ter pelo menos 6 caracteres.');
      return false;
    }

    if (password !== confirmPassword) {
      Alert.alert('Erro', 'As senhas não correspondem.');
      return false;
    }

    return true;
  };

  const handleSignUp = () => {
    if (validateFields()) {
      // Processar cadastro
      // Adicione aqui a lógica para processar o cadastro
      // Navegar para a tela de login após o cadastro bem-sucedido
      navigation.navigate('Login');
    }
  };

  return (
    <KeyboardAvoidingView behavior={Platform.OS === 'ios' ? 'padding' : 'height'}>
      <Pressable
        style={styles.container}
        onPress={Keyboard.dismiss}
      >
        <View style={styles.container}>
          <Image
            source={require('./src/img/LOGO.png')}
            style={styles.logo}
          />

          <TextInput
            placeholder='Celular, username ou email'
            style={styles.input}
            value={username}
            onChangeText={setUsername}
          />

          <TextInput
            placeholder='Sua senha'
            style={styles.input}
            value={password}
            onChangeText={setPassword}
            secureTextEntry
          />

          <TextInput
            placeholder='Confirme sua senha'
            style={styles.input}
            value={confirmPassword}
            onChangeText={setConfirmPassword}
            secureTextEntry
          />

          <Pressable style={styles.loginButton} onPress={handleSignUp}>
            <Text style={styles.loginText}>Cadastrar</Text>
          </Pressable>
        </View>
      </Pressable>
    </KeyboardAvoidingView>
  );
};
