import React, { useState } from 'react';
import {
  StyleSheet,
  Text,
  View,
  Image,
  TextInput,
  Pressable,
  Platform,
  KeyboardAvoidingView,
  Keyboard,
  Alert,
} from 'react-native';

export default function App({ navigation }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isSignUp, setIsSignUp] = useState(false); // Adicionado estado para controlar se está na tela de cadastro

  const validateFields = () => {
    if (!username.trim()) {
      Alert.alert('Erro', 'Por favor, insira o celular, nome de usuário ou email.');
      return false;
    }

    if (!password.trim()) {
      Alert.alert('Erro', 'Por favor, insira a senha.');
      return false;
    }

    if (password.length < 6) {
      Alert.alert('Erro', 'A senha deve ter pelo menos 6 caracteres.');
      return false;
    }

    return true;
  };

  const handleLogin = () => {
    if (validateFields()) {
      // Adicione aqui a lógica para processar o login
      // Se o login for bem-sucedido, navegue para a tela Home
      navigation.navigate('Home');
    }
  };

  const handleSignUp = () => {
    // Adicione aqui a lógica para processar o cadastro
    // Se o cadastro for bem-sucedido, volte para a tela de login
    setIsSignUp(false);
  };

  const handleNavigateToSignUp = () => {
    // Navegue para a tela de cadastro
    setIsSignUp(true);
  };

  return (
    <KeyboardAvoidingView behavior={Platform.OS === 'ios' ? 'padding' : 'height'}>
      <Pressable
        style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}
        onPress={Keyboard.dismiss}
      >
        <View style={styles.container}>
          {/* Restante do seu código... */}

          {isSignUp ? (
            // Tela de cadastro
            <View>
              {/* Seu formulário de cadastro */}
              <Pressable style={styles.loginButton} onPress={handleSignUp}>
                <Text style={styles.loginText}>Cadastrar</Text>
              </Pressable>
              <View style={styles.signUpContainer}>
                <Text style={styles.signUpText}>Já possui uma conta?</Text>
                <Pressable onPress={() => setIsSignUp(false)}>
                  <Text style={styles.signUpButton}>Login!</Text>
                </Pressable>
              </View>
            </View>
          ) : (
            // Tela de login
            <View>
              {/* Seu formulário de login */}
              <Pressable style={styles.loginButton} onPress={handleLogin}>
                <Text style={styles.loginText}>Acessar</Text>
              </Pressable>
              <View style={styles.signUpContainer}>
                <Text style={styles.signUpText}>Não possui uma conta?</Text>
                <Pressable onPress={handleNavigateToSignUp}>
                  <Text style={styles.signUpButton}>Cadastre-se!</Text>
                </Pressable>
              </View>
            </View>
          )}
        </View>
      </Pressable>
    </KeyboardAvoidingView>
  );
}

// O resto do seu código permanece inalterado


const styles = StyleSheet.create({
  container: {
    backgroundColor: '#333',
    alignItems: 'center',
    justifyContent: 'center',
    paddingBottom: 350,
  },
  logo: {
    width: '100%',
    height: '50%',
    paddingTop: '50%',
    marginTop: Platform.OS === 'android' ? '13%' : '60%',
    marginBottom: Platform.OS === 'android' ? '13%' : '15%',
    resizeMode: 'contain',
  },
  input: {
    width: '87%',
    height: 35,
    backgroundColor: '#999',
    marginBottom: 10,
    padding: 8,
    borderRadius: 5,
  },
  loginButton: {
    marginTop: '5%',
    backgroundColor: '#8a08bb',
    width: '87%',
    height: 35,
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 5,
  },
  loginText: {
    color: '#fff',
    fontSize: 17,
  },
  signUpContainer: {
    marginTop: '5%',
    alignItems: 'center',
  },
  signUpText: {
    marginTop: 20,
    color: '#c4c4c4',
    paddingRight: 5,
  },
  signUpButton: {
    color: '#8a08bb',
    fontWeight: 'bold',
  },
});
