import { StatusBar } from 'expo-status-bar';
import React from 'react';
import {
  StyleSheet,
  Text,
  View,
  Image,
  TextInput,
  TouchableOpacity,
  Platform,
  KeyboardAvoidingView,
  Keyboard,
  TouchableWithoutFeedback,
} from 'react-native';

export default function App() {
  return (
    <KeyboardAvoidingView behavior={Platform.OS === 'ios' ? 'padding' : 'height'}>
      <TouchableWithoutFeedback onPress={Keyboard.dismiss}>
        <View style={styles.container}>
          <StatusBar backgroundColor="#fff" translucent={false} />
          <Image source={require('./src/img/color_With_.png')} style={styles.logo} />
          <TextInput placeholder='Celular, username ou email' style={styles.input} />
          <TextInput placeholder='Sua senha' style={styles.input} />

          <TouchableOpacity style={styles.loginButton}>
            <Text style={styles.loginText}>Acessar</Text>
          </TouchableOpacity>

          <View style={styles.signUpContainer}>
            <Text style={styles.signUpText}>NÃ£o possui uma conta?</Text>
            <TouchableOpacity>
              <Text style={styles.signUpButton}>Cadastre-se!</Text>
            </TouchableOpacity>
          </View>
        </View>
      </TouchableWithoutFeedback>
    </KeyboardAvoidingView>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#333',
    alignItems: 'center',
    justifyContent: 'center',
    paddingBottom: 350
  },
  logo: {
    width:'100%',
    height:'20%',
    paddingTop: '50%',
    marginTop: Platform.OS === 'android' ? '13%' : '60%',
    marginBottom: Platform.OS === 'android' ? '13%' : '15%',
    resizeMode: 'contain'
  },
  input: {
    width: '87%',
    height: 35,
    backgroundColor: '#999',
    marginBottom: 10,
    padding: 8,
    borderRadius: 5,
  },
  loginButton: {
    marginTop: '5%',
    backgroundColor: '#8a08bb',
    width: '87%',
    height: 35,
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 5,
  },
  loginText: {
    color: '#fff',
    fontSize: 17,
  },
  signUpContainer: {
    marginTop: '5%',
    alignItems: "center"
  },
  signUpText: {
    marginTop: '20',
    color: '#c4c4c4',
    paddingRight: 5, 
    
  },
  signUpButton: {
    color: '#8a08bb',
    fontWeight: 'bold'
  },
});
