import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import {
  StyleSheet,
  Text,
  View,
  Image,
  TextInput,
  Pressable,
  Platform,
  KeyboardAvoidingView,
  Keyboard,
  Alert,
} from 'react-native';

export default function App() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isRegistered, setIsRegistered] = useState(false);

  const validateFields = () => {
    if (!username.trim()) {
      Alert.alert('Erro', 'Por favor, insira o celular, nome de usuário ou email.');
      return false;
    }

    if (!password.trim()) {
      Alert.alert('Erro', 'Por favor, insira a senha.');
      return false;
    }

    if (password.length < 6) {
      Alert.alert('Erro', 'A senha deve ter pelo menos 6 caracteres.');
      return false;
    }

    return true;
  };

  const handleLogin = () => {
    if (validateFields()) {
      if (isRegistered) {
        // Processar login
        // Adicione aqui a lógica para processar o login
        // Navegar para a página inicial
      } else {
        Alert.alert('Erro', 'Login não existe. Por favor, cadastre-se primeiro.');
      }
    }
  };

  const handleRegister = () => {
    if (validateFields()) {
      // Processar registro
      // Adicione aqui a lógica para processar o registro
      setIsRegistered(true);
      setUsername('');
      setPassword('');
      Alert.alert('Sucesso', 'Cadastro feito com sucesso!');
      // Navegar para a página de login
    }
  };

  return (
    <KeyboardAvoidingView behavior={Platform.OS === 'ios' ? 'padding' : 'height'}>
      <Pressable
        style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}
        onPress={Keyboard.dismiss}
      >
        <View style={styles.container}>
          <StatusBar backgroundColor="#fff" translucent={false} />

          <Image
            source={require('./src/img/LOGO.png')}
            style={styles.logo}
          />

          <TextInput
            placeholder='Celular, username ou email'
            style={styles.input}
            value={username}
            onChangeText={setUsername}
          />

          <TextInput
            placeholder='Sua senha'
            style={styles.input}
            value={password}
            onChangeText={setPassword}
            secureTextEntry
          />

          <Pressable style={styles.loginButton} onPress={handleLogin}>
            <Text style={styles.loginText}>Acessar</Text>
          </Pressable>

          <Pressable style={styles.loginButton} onPress={handleRegister}>
            <Text style={styles.loginText}>Cadastre-se!</Text>
          </Pressable>
        </View>
      </Pressable>
    </KeyboardAvoidingView>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#333',
    alignItems: 'center',
    justifyContent: 'center',
    paddingBottom: 350,
  },
  logo: {
    width: '100%',
    height: '50%',
    paddingTop: '50%',
    marginTop: Platform.OS === 'android' ? '13%' : '60%',
    marginBottom: Platform.OS === 'android' ? '13%' : '15%',
    resizeMode: 'contain',
  },
  input: {
    width: '87%',
    height: 35,
    backgroundColor: '#999',
    marginBottom: 10,
    padding: 8,
    borderRadius: 5,
  },
  loginButton: {
    marginTop: '5%',
    backgroundColor: '#8a08bb',
    width: '87%',
    height: 35,
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 5,
  },
  loginText: {
    color: '#fff',
    fontSize: 17,
  },
});
