const styles = StyleSheet.create({
  background: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'flex-start',
    backgroundColor: '#2E2E2E',
  },
  titleImage: {
    flex: 1,
    width: '100%',
    marginBottom: 200,
    marginTop: -180,
  },
  overlay: {
    flex: 1,
    backgroundColor: 'rgba(0, 0, 0, 0.0)',
  },
  formContainer: {
    marginTop: 750,
    alignItems: 'center',
    marginBottom: 20,
  },
  input: {
    width: '70%',
    height: 30,
    backgroundColor: '#FFFFFF',
    marginBottom: 10,
    color: '#000000',
    paddingHorizontal: 10,
    borderRadius: 8,
  },
  buttonLogin: {
    backgroundColor: '#8908bb',
    height: 30,
    width: '70%',
    borderRadius: 8,
    marginTop: 0,
    justifyContent: 'center',
  },
  buttonSignup: {
    backgroundColor: 'transparent',
    height: 30,
    width: 100,
    borderRadius: 8,
    marginTop: 15,
  },
  buttonText: {
    color: '#FFFFFF',
    textAlign: 'center',
    backgroundColor: 'transparent',
    fontSize: 15,
    marginTop: -5,
  },
  errorInput: {
    borderColor: '#DBA901',
  },
  labelError: {
    alignSelf: 'center',
    color: '#FF0000',
    marginBottom: 8,
  },
  messageText: {
    marginTop: 10,
    color: '#848484',
    fontSize: 15,
  },
});

function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Login">
        <Stack.Screen name="Login" component={LoginScreen} />
        <Stack.Screen name="Profile" component={ProfileScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

export default App;
